val springbootManagementVersions = "1.1.7"
val avroPluginVersion = "1.9.1"
val nodePluginVersion = "7.1.0"

dependencies {
    // https://mvnrepository.com/artifact/org.springframework.boot/spring-boot-gradle-plugin
